package com.syntax.class07;

public class TaskWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=1;
		
		while(a<=100) {
			if(a%2==0) {
			System.out.print(a+" ");
		}
			a++;
		}System.out.println();
		
		
		
		while(a>=1) {
			if(a%3==1) {
			System.out.print(a+" ");
		}
			a--;
	}
	}
}
