package com.syntax.class08;

import java.util.Scanner;

public class ReplitTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x;
	    Scanner scan=new Scanner(System.in);
	   System.out.println("In:");
	    x=scan.nextInt();
	    

	    for(int y=x-1; y<=0; y--){
	      System.out.print(y+" ");
	      
	    }
	    
	    
	    		
	}

}
