package com.syntax.class13;

public class StringDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str="Bacth 13 is good actually very good ";
		System.out.println(str.replace("good","Great"));
		System.out.println(str.replace("g","G"));//replace all the (g)
		System.out.println(str.replaceFirst("g","G"));//replace only the first(g).
	
		
		
		
	}

}
